import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Order {
  id: number;
  order_number: string;
  supplier: string;
  total_amount_excl_vat: number;
  status: string;
  date_received: string;
}

const formatCurrency = (amount: number | null | undefined) => {
  if (amount === null || amount === undefined) {
    return '€ 0,00';
  }
  return new Intl.NumberFormat('nl-NL', {
    style: 'currency',
    currency: 'EUR',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  }).format(amount);
};

const Orders: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const handleExport = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/orders/export', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob', // Belangrijk voor het downloaden van bestanden
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;

      const contentDisposition = response.headers['content-disposition'];
      let fileName = 'orders-export.csv';
      if (contentDisposition) {
        const fileNameMatch = contentDisposition.match(/filename="?(.+)"?/);
        if (fileNameMatch.length === 2)
          fileName = fileNameMatch[1];
      }

      link.setAttribute('download', fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();

    } catch (err) {
      console.error('Export mislukt', err);
      setError('Exporteren van de data is mislukt.');
    }
  };

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setIsLoading(true);
        const token = localStorage.getItem('token');

        if (!token) {
          setError('Geen toegangstoken gevonden. Log opnieuw in.');
          setIsLoading(false);
          return;
        }

        const response = await axios.get('/api/orders', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setOrders(response.data);
      } catch (err: any) {
        setError(err.response?.data?.error || 'Kon orders niet ophalen');
      } finally {
        setIsLoading(false);
      }
    };
    fetchOrders();
  }, []);

  const getStatusStyle = (status: string) => {
    const baseStyle = {
      padding: '3px 8px',
      borderRadius: '8px',
      fontSize: '11px',
      fontWeight: '500',
      textAlign: 'center' as const,
      color: 'white',
      whiteSpace: 'nowrap' as const,
    };

    switch (status.toLowerCase()) {
      case 'compleet': return { ...baseStyle, backgroundColor: '#28a745' };
      case 'in behandeling': return { ...baseStyle, backgroundColor: '#ffc107', color: '#333' };
      case 'geannuleerd': return { ...baseStyle, backgroundColor: '#dc3545' };
      case 'retour': return { ...baseStyle, backgroundColor: '#6f42c1' };
      default: return { ...baseStyle, backgroundColor: '#6c757d' };
    }
  };

  if (isLoading) {
    return (
      <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '10px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
        <div style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', margin: '0 auto', padding: '20px', textAlign: 'center' }}>
          <div style={{ fontSize: '16px', color: '#6c757d' }}>🔄 Orders laden...</div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '10px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', margin: '0 auto', maxWidth: '100%', padding: '15px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '20px', flexWrap: 'wrap', gap: '15px' }}>
          <div>
            <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '20px' }}>📦 Bestellingen</h1>
            <p style={{ margin: '2px 0 10px 0', color: '#6c757d', fontSize: '12px' }}>Overzicht van al je bestellingen</p>
            <button onClick={() => navigate('/add-order')} style={{ backgroundColor: '#007bff', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
              ➕ Nieuwe Bestelling
            </button>
          </div>
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: '10px' }}>
            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <button onClick={() => navigate('/change-password')} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500', whiteSpace: 'nowrap' }}>
                  Wachtwoord wijzigen
                </button>
                <button onClick={handleLogout} style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500', whiteSpace: 'nowrap' }}>
                  Uitloggen
                </button>
            </div>
            <button onClick={handleExport} style={{ backgroundColor: '#17a2b8', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500', whiteSpace: 'nowrap' }}>
              Exporteer naar CSV
            </button>
          </div>
        </div>

        {error && <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '10px', borderRadius: '6px', marginBottom: '15px', border: '1px solid #f5c6cb', fontSize: '13px' }}>⚠️ {error}</div>}

        {orders.length === 0 && !error ? (
          <div style={{ textAlign: 'center', padding: '30px 20px', color: '#6c757d' }}>
            <div style={{ fontSize: '36px', marginBottom: '15px' }}>📭</div>
            <h3 style={{ margin: '0 0 10px 0', fontSize: '16px' }}>Geen bestellingen gevonden</h3>
            <p style={{ margin: '0 0 15px 0', fontSize: '13px' }}>Er zijn nog geen bestellingen toegevoegd.</p>
            <button onClick={() => navigate('/add-order')} style={{ backgroundColor: '#007bff', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
              ➕ Eerste Bestelling
            </button>
          </div>
        ) : (
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', minWidth: '600px' }}>
              <thead>
                <tr style={{ borderBottom: '2px solid #dee2e6' }}>
                  <th style={{ padding: '8px 6px', textAlign: 'left', color: '#495057', fontSize: '12px', fontWeight: '600' }}>Order</th>
                  <th style={{ padding: '8px 6px', textAlign: 'left', color: '#495057', fontSize: '12px', fontWeight: '600' }}>Leverancier</th>
                  <th style={{ padding: '8px 6px', textAlign: 'left', color: '#495057', fontSize: '12px', fontWeight: '600' }}>Bedrag</th>
                  <th style={{ padding: '8px 6px', textAlign: 'left', color: '#495057', fontSize: '12px', fontWeight: '600' }}>Datum</th>
                  <th style={{ padding: '8px 6px', textAlign: 'center', color: '#495057', fontSize: '12px', fontWeight: '600' }}>Status</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(order => (
                  <tr key={order.id} style={{ borderBottom: '1px solid #e9ecef', cursor: 'pointer' }} onClick={() => navigate(`/orders/${order.id}`)}>
                    <td style={{ padding: '8px 6px', fontWeight: '500', color: '#007bff', fontSize: '13px' }}>{order.order_number}</td>
                    <td style={{ padding: '8px 6px', fontSize: '13px' }}>{order.supplier}</td>
                    <td style={{ padding: '8px 6px', fontSize: '13px' }}>{formatCurrency(order.total_amount_excl_vat)}</td>
                    <td style={{ padding: '8px 6px', fontSize: '13px' }}>{order.date_received ? new Date(order.date_received).toLocaleDateString('nl-NL') : 'N/A'}</td>
                    <td style={{ padding: '8px 6px' }}><div style={{ display: 'flex', justifyContent: 'center' }}><span style={getStatusStyle(order.status)}>{order.status}</span></div></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Orders;
