import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditOrder: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [formData, setFormData] = useState<any>({
    order_number: '', invoice_number: '', date_quote_requested: '',
    date_invoice_received: '', date_order_placed: '', date_payment_completed: '',
    date_shipped: '', date_received: '', total_amount_excl_vat: '',
    total_vat: '', total_import_duties: '', total_clearance_costs: '',
    vat_clearance_costs: '', date_booked_accounting: '', supplier: '',
    webshop: '', description: '', is_return: false
  });
  const [checkboxes, setCheckboxes] = useState<any>({});
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const parseLocaleString = (str: string): number | null => {
    if (typeof str !== 'string' || str.trim() === '') return null;
    const number = parseFloat(str.replace(/\./g, '').replace(',', '.'));
    return isNaN(number) ? null : number;
  };

  const formatToLocaleString = (num: number | null): string => {
    if (num === null || num === undefined) return '';
    return new Intl.NumberFormat('nl-NL', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(num);
  };

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          setError('Geen toegangstoken gevonden. Log opnieuw in.');
          return;
        }

        const response = await axios.get(`/api/orders/${id}`, { headers: { Authorization: `Bearer ${token}` } });

        const orderData = response.data;

        const newFormData: any = {};
        const newCheckboxes: any = {};

        Object.keys(orderData).forEach(key => {
          if (key.startsWith('date_')) {
            const dateValue = orderData[key];
            newFormData[key] = dateValue ? new Date(dateValue).toISOString().split('T')[0] : '';

            const checkboxKey = key.replace('date_', '');
            newCheckboxes[checkboxKey] = !!dateValue;
          } else if (key.startsWith('total_') || key.startsWith('vat_')) {
            newFormData[key] = formatToLocaleString(orderData[key]);
          } 
          else {
            newFormData[key] = orderData[key];
          }
        });

        setFormData(newFormData);
        setCheckboxes(newCheckboxes);
      } catch (err: any) {
        setError(err.response?.data?.error || 'Order ophalen mislukt');
      }
    };
    fetchOrder();
  }, [id]);

  const handleCheckboxChange = (field: string, dateField: string) => {
    const isChecked = !checkboxes[field];
    setCheckboxes((prev: any) => ({ ...prev, [field]: isChecked }));
    setFormData((prev: any) => ({ ...prev, [dateField]: isChecked ? new Date().toISOString().split('T')[0] : '' }));
  };

  const handleCurrencyChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev: any) => ({ ...prev, [name]: value }));
  };

  const handleCurrencyBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const numericValue = parseLocaleString(value);
    setFormData((prev: any) => ({ ...prev, [name]: formatToLocaleString(numericValue) }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setMessage('');
    setError('');
    setIsLoading(true);

    const dataToSubmit = { ...formData };
    Object.keys(dataToSubmit).forEach(key => {
        if (key.startsWith('total_') || key.startsWith('vat_')) {
            dataToSubmit[key] = parseLocaleString(dataToSubmit[key]) || 0;
        }
    });

    try {
      const token = localStorage.getItem('token');
      await axios.put(`/api/orders/${id}`, dataToSubmit, { headers: { Authorization: `Bearer ${token}` } });
      setMessage('Order succesvol bijgewerkt! Je wordt teruggestuurd...');
      setTimeout(() => navigate('/orders'), 2000);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Order bijwerken mislukt');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Weet je zeker dat je deze bestelling wilt verwijderen? Dit kan niet ongedaan worden gemaakt.')) {
        if (window.confirm('Definitief verwijderen?')) {
            setIsLoading(true);
            try {
                const token = localStorage.getItem('token');
                await axios.delete(`/api/orders/${id}`, { headers: { Authorization: `Bearer ${token}` } });
                setMessage('Order succesvol verwijderd. Je wordt teruggestuurd...');
                setTimeout(() => navigate('/orders'), 2000);
            } catch (err: any) {
                setError(err.response?.data?.error || 'Verwijderen van order mislukt.');
                setIsLoading(false);
            }
        }
    }
  };

  const formSectionStyle = { marginBottom: '20px' };
  const formSectionHeaderStyle = { color: '#34495e', marginBottom: '15px', fontSize: '16px', borderBottom: '1px solid #e9ecef', paddingBottom: '8px' };
  const inputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', boxSizing: 'border-box' as const, fontSize: '14px' };
  const labelStyle = { display: 'block', marginBottom: '5px', fontWeight: '500', color: '#495057', fontSize: '13px' };
  const checkboxStyle = { marginRight: '8px', transform: 'scale(1.2)' };
  const checkboxContainerStyle = { display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '10px' };
  const dateInputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', marginTop: '5px' };

  const renderCurrencyInput = (name: string, label: string) => (
    <div style={{ marginBottom: '15px' }}>
      <label style={labelStyle}>{label}</label>
      <input
        type="text"
        inputMode="decimal"
        name={name}
        value={formData[name] || ''}
        onChange={handleCurrencyChange}
        onBlur={handleCurrencyBlur}
        style={inputStyle}
        placeholder="0,00"
      />
    </div>
  );

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '10px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', margin: '0 auto', maxWidth: '100%', padding: '15px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px', flexWrap: 'wrap', gap: '10px' }}>
          <div>
            <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '18px' }}>âœï¸ Bestelling Bewerken</h1>
            <p style={{ margin: '2px 0 0 0', color: '#6c757d', fontSize: '12px' }}>Pas de details van de bestelling aan</p>
          </div>
          <button onClick={() => navigate('/orders')} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '8px 12px', borderRadius: '6px', cursor: 'pointer', fontSize: '12px', fontWeight: '500', whiteSpace: 'nowrap' }}>
            â† Terug
          </button>
        </div>

        {message && <div style={{ backgroundColor: '#d4edda', color: '#155724', padding: '10px', borderRadius: '6px', marginBottom: '15px', border: '1px solid #c3e6cb', fontSize: '13px' }}>âœ… {message}</div>}
        {error && <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '10px', borderRadius: '6px', marginBottom: '15px', border: '1px solid #f5c6cb', fontSize: '13px' }}>âš ï¸ {error}</div>}

        <form onSubmit={handleSubmit}>
           <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>â„¹ï¸ Basis Informatie</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px' }}>
              <div>
                <label style={labelStyle}>Ordernummer *</label>
                <input type="text" name="order_number" value={formData.order_number} onChange={(e) => setFormData({...formData, order_number: e.target.value})} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Leverancier *</label>
                <input type="text" name="supplier" value={formData.supplier} onChange={(e) => setFormData({...formData, supplier: e.target.value})} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Factuurnummer</label>
                <input type="text" name="invoice_number" value={formData.invoice_number} onChange={(e) => setFormData({...formData, invoice_number: e.target.value})} disabled={!checkboxes.invoice_received} style={{...inputStyle, backgroundColor: checkboxes.invoice_received ? 'white' : '#e9ecef'}} />
              </div>
              <div>
                <label style={labelStyle}>Webshop</label>
                <input type="text" name="webshop" value={formData.webshop} onChange={(e) => setFormData({...formData, webshop: e.target.value})} style={inputStyle} />
              </div>
            </div>
          </div>

          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>ğŸ“… Proces & Datums</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_requested} onChange={() => handleCheckboxChange('quote_requested', 'date_quote_requested')} style={checkboxStyle} />ğŸ“‹ Offerte aangevraagd</label>{checkboxes.quote_requested && <input type="date" name="date_quote_requested" value={formData.date_quote_requested} onChange={(e) => setFormData({...formData, date_quote_requested: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.order_placed} onChange={() => handleCheckboxChange('order_placed', 'date_order_placed')} style={checkboxStyle} />ğŸ›’ Order geplaatst</label>{checkboxes.order_placed && <input type="date" name="date_order_placed" value={formData.date_order_placed} onChange={(e) => setFormData({...formData, date_order_placed: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.payment_completed} onChange={() => handleCheckboxChange('payment_completed', 'date_payment_completed')} style={checkboxStyle} />ğŸ’³ Betaling voltooid</label>{checkboxes.payment_completed && <input type="date" name="date_payment_completed" value={formData.date_payment_completed} onChange={(e) => setFormData({...formData, date_payment_completed: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.invoice_received} onChange={() => handleCheckboxChange('invoice_received', 'date_invoice_received')} style={checkboxStyle} />ğŸ§¾ Factuur ontvangen</label>{checkboxes.invoice_received && <input type="date" name="date_invoice_received" value={formData.date_invoice_received} onChange={(e) => setFormData({...formData, date_invoice_received: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.shipped} onChange={() => handleCheckboxChange('shipped', 'date_shipped')} style={checkboxStyle} />ğŸ“¦ Verzonden</label>{checkboxes.shipped && <input type="date" name="date_shipped" value={formData.date_shipped} onChange={(e) => setFormData({...formData, date_shipped: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.received} onChange={() => handleCheckboxChange('received', 'date_received')} style={checkboxStyle} />âœ… Ontvangen</label>{checkboxes.received && <input type="date" name="date_received" value={formData.date_received} onChange={(e) => setFormData({...formData, date_received: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" name="is_return" checked={formData.is_return} onChange={(e) => setFormData({...formData, is_return: e.target.checked})} style={checkboxStyle} />ğŸ”„ Retour</label></div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.booked_accounting} onChange={() => handleCheckboxChange('booked_accounting', 'date_booked_accounting')} style={checkboxStyle} />ğŸ“Š Geboekt in administratie</label>{checkboxes.booked_accounting && <input type="date" name="date_booked_accounting" value={formData.date_booked_accounting} onChange={(e) => setFormData({...formData, date_booked_accounting: e.target.value})} style={dateInputStyle} />}</div>
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
            <div>
              <h3 style={formSectionHeaderStyle}>ğŸ’° FinanciÃ«n</h3>
              {renderCurrencyInput('total_amount_excl_vat', 'Totaalbedrag excl. BTW')}
              {renderCurrencyInput('total_vat', 'Totale BTW')}
              {renderCurrencyInput('total_import_duties', 'Totale invoerrechten')}
              {renderCurrencyInput('total_clearance_costs', 'Totale inklaringskosten')}
              {renderCurrencyInput('vat_clearance_costs', 'BTW inklaringskosten')}
            </div>
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <h3 style={formSectionHeaderStyle}>ğŸ“ Omschrijving en opmerkingen</h3>
              <textarea 
                name="description"
                value={formData.description} 
                onChange={(e) => setFormData({...formData, description: e.target.value})} 
                style={{ ...inputStyle, flexGrow: 1, resize: 'vertical', minHeight: '150px' }} 
              />
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', paddingTop: '20px', marginTop: '15px', borderTop: '1px solid #e9ecef', flexWrap: 'wrap', gap: '10px' }}>
            <button type="button" onClick={handleDelete} style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
              ğŸ—‘ï¸ Verwijderen
            </button>
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
              <button type="button" onClick={() => navigate('/orders')} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
                âŒ Annuleren
              </button>
              <button type="submit" disabled={isLoading} style={{ backgroundColor: isLoading ? '#6c757d' : '#007bff', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: isLoading ? 'not-allowed' : 'pointer', fontSize: '13px', fontWeight: '500' }}>
                {isLoading ? 'ğŸ”„ Opslaan...' : 'ğŸ’¾ Opslaan'}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditOrder;
