import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

interface Order {
  id: number;
  order_number: string;
  invoice_number: string;
  supplier: string;
  webshop: string;
  description: string;
  total_amount_excl_vat: string;
  total_vat: string;
  total_import_duties: string;
  total_clearance_costs: string;
  vat_clearance_costs: string;
  total_price: string;
  is_return: boolean;
  date_invoice_received: string;
  date_quote_requested: string;
  date_quote_received: string;
  date_order_placed: string;
  date_payment_completed: string;
  date_shipped: string;
  date_received: string;
  date_quality_control: string;
  date_booked_accounting: string;
  date_is_returned: string;
  date_refunded: string;
  date_return_processed_admin: string;
}

const EditOrder: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const [formData, setFormData] = useState<Partial<Order>>({
    order_number: '',
    invoice_number: '',
    supplier: '',
    webshop: '',
    description: '',
    total_amount_excl_vat: '',
    total_vat: '',
    total_import_duties: '',
    total_clearance_costs: '',
    vat_clearance_costs: '',
    is_return: false,
    date_invoice_received: '',
    date_quote_requested: '',
    date_quote_received: '',
    date_order_placed: '',
    date_payment_completed: '',
    date_shipped: '',
    date_received: '',
    date_quality_control: '',
    date_booked_accounting: '',
    date_is_returned: '',
    date_refunded: '',
    date_return_processed_admin: '',
  });

  const [initialFormData, setInitialFormData] = useState<Partial<Order> | null>(null);

  const [checkboxes, setCheckboxes] = useState({
    quote_requested: false,
    quote_received: false,
    order_placed: false,
    payment_completed: false,
    invoice_received: false,
    shipped: false,
    received: false,
    quality_control: false,
    booked_accounting: false,
    is_returned: false,
    refunded: false,
    return_processed_admin: false,
  });

  const [isLoading, setIsLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const parseLocaleString = (str: string | number): number | null => {
    if (typeof str === 'number') return str;
    if (!str || typeof str !== 'string') return null;
    return parseFloat(str.replace(/\./g, '').replace(',', '.'));
  };

  const formatToLocaleString = (num: number | null): string => {
    if (num === null || num === undefined || num === 0) return '';
    return new Intl.NumberFormat('nl-NL', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(num);
  };

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`/api/orders/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        const data: Order = response.data;

        const newFormData: Partial<Order> = Object.keys(formData).reduce((acc, key) => {
          const typedKey = key as keyof Order;
          if (data[typedKey] !== null && data[typedKey] !== undefined) {
            if (typeof data[typedKey] === 'number' && ['total_amount_excl_vat', 'total_vat', 'total_import_duties', 'total_clearance_costs', 'vat_clearance_costs'].includes(typedKey)) {
              if (data[typedKey] !== 0) {
                (acc as any)[typedKey] = new Intl.NumberFormat('nl-NL', { minimumFractionDigits: 2, maximumFractionDigits: 2 }).format(data[typedKey] as number);
              } else {
                (acc as any)[typedKey] = '';
              }
            } else {
              (acc as any)[typedKey] = data[typedKey];
            }
          }
          return acc;
        }, {} as Partial<Order>);


        setFormData({ ...newFormData });
        setInitialFormData({ ...newFormData });

        const newCheckboxes = {
          quote_requested: !!data.date_quote_requested,
          quote_received: !!data.date_quote_received,
          order_placed: !!data.date_order_placed,
          payment_completed: !!data.date_payment_completed,
          invoice_received: !!data.date_invoice_received,
          shipped: !!data.date_shipped,
          received: !!data.date_received,
          quality_control: !!data.date_quality_control,
          booked_accounting: !!data.date_booked_accounting,
          is_returned: !!data.date_is_returned,
          refunded: !!data.date_refunded,
          return_processed_admin: !!data.date_return_processed_admin,
        };
        setCheckboxes(newCheckboxes);

      } catch (err: any) {
        setError(err.response?.data?.error || 'Order ophalen mislukt');
      } finally {
        setIsLoading(false);
      }
    };
    fetchOrder();
  }, [id]);

  const handleCheckboxChange = (checkboxName: keyof typeof checkboxes, dateFieldName: keyof Order) => {
    const isChecked = !checkboxes[checkboxName];
    setCheckboxes(prev => ({ ...prev, [checkboxName]: isChecked }));
    if (isChecked) {
      setFormData(prev => ({ ...prev, [dateFieldName]: new Date().toISOString().split('T')[0] }));
    } else {
      setFormData(prev => ({ ...prev, [dateFieldName]: '' }));
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value, type } = e.target;
    const key = name as keyof Order;
    if (type === 'checkbox') {
        const { checked } = e.target as HTMLInputElement;
        setFormData(prev => ({ ...prev, [key]: checked }));
    } else {
        setFormData(prev => ({ ...prev, [key]: value }));
    }
  };

  const handleCurrencyChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const key = name as keyof Order;
    setFormData(prev => ({ ...prev, [key]: value }));
  };

  const handleBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const key = name as keyof Order;
    const numericValue = parseLocaleString(value);
    setFormData(prev => ({ ...prev, [key]: formatToLocaleString(numericValue) }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setMessage('');
    setError('');
    setIsLoading(true);

    const dataToSubmit: { [key: string]: any } = { ...formData };
    Object.keys(dataToSubmit).forEach(key => {
      const typedKey = key as keyof Order;
      if ((typedKey.startsWith('total_') || typedKey.startsWith('vat_')) && typeof dataToSubmit[typedKey] === 'string') {
        dataToSubmit[typedKey] = parseLocaleString(dataToSubmit[typedKey] as string) || null;
      }
    });

    try {
      const token = localStorage.getItem('token');
      await axios.put(`/api/orders/${id}`, dataToSubmit, { headers: { Authorization: `Bearer ${token}` } });
      setMessage('Order succesvol bijgewerkt! Je wordt teruggestuurd...');
      setTimeout(() => navigate('/orders'), 2000);
    } catch (err: any) {
      setError(err.response?.data?.error || 'Order bijwerken mislukt');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Weet je zeker dat je deze bestelling wilt verwijderen? Dit kan niet ongedaan worden gemaakt.')) {
        if (window.confirm('Definitief verwijderen?')) {
            setIsLoading(true);
            try {
                const token = localStorage.getItem('token');
                await axios.delete(`/api/orders/${id}`, { headers: { Authorization: `Bearer ${token}` } });
                setMessage('Order succesvol verwijderd. Je wordt teruggestuurd...');
                setTimeout(() => navigate('/orders'), 2000);
            } catch (err: any) {
                setError(err.response?.data?.error || 'Verwijderen van order mislukt.');
                setIsLoading(false);
            }
        }
    }
  };

  const formSectionStyle = { marginBottom: '20px' };
  const formSectionHeaderStyle = { color: '#34495e', marginBottom: '15px', fontSize: '16px', borderBottom: '1px solid #e9ecef', paddingBottom: '8px' };
  const inputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', boxSizing: 'border-box' as const, fontSize: '14px' };
  const labelStyle = { display: 'block', marginBottom: '5px', fontWeight: '500', color: '#495057', fontSize: '13px' };
  const checkboxStyle = { marginRight: '8px', transform: 'scale(1.2)' };
  const checkboxContainerStyle = { display: 'flex', alignItems: 'center', gap: '8px' };
  const dateInputStyle = { padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', fontSize: '14px', width: 'auto' };
  const checkboxWrapperStyle = {
    display: 'flex',
    flexDirection: 'column' as const,
    gap: '8px',
  };

  const renderCurrencyInput = (name: keyof Order, label: string) => (
    <div style={{ marginBottom: '15px' }}>
      <label style={labelStyle}>{label}</label>
      <input
        type="text"
        inputMode="decimal"
        name={name}
        value={(() => {
          const value = formData[name];
          if (value === undefined || value === null || value === '' || value === '0' || value === '0,00' || value === 0) {
            return '';
          }
          return String(value);
        })()}
        onChange={handleCurrencyChange}
        onBlur={handleBlur}
        style={inputStyle}
        placeholder="0,00"
      />
    </div>
  );

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '10px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '20px'}}>
          <img src="/logo.png" alt="Lion Gris Logo" style={{ width: '50px', height: 'auto' }} />
          <div>
            <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '18px' }}>âœï¸ Bestelling Bewerken</h1>
            <p style={{ margin: '2px 0 0 0', color: '#6c757d', fontSize: '12px' }}>Pas de details van de bestelling aan</p>
          </div>
        </div>
        <form onSubmit={handleSubmit}>
           <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>â„¹ï¸ Basis Informatie</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
              <div>
                <label style={labelStyle}>Ordernummer *</label>
                <input type="text" name="order_number" value={formData.order_number} onChange={handleChange} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Factuurnummer</label>
                <input type="text" name="invoice_number" value={formData.invoice_number} onChange={handleChange} style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Leverancier *</label>
                <input type="text" name="supplier" value={formData.supplier} onChange={handleChange} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Webshop</label>
                <input type="text" name="webshop" value={formData.webshop} onChange={handleChange} style={inputStyle} />
              </div>
            </div>
          </div>

          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>ðŸ“… Proces & Datums</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '20px' }}>
              <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_requested} onChange={() => handleCheckboxChange('quote_requested', 'date_quote_requested')} style={checkboxStyle} />ðŸ“‹ Offerte aangevraagd</label>{checkboxes.quote_requested && <input type="date" name="date_quote_requested" value={formData.date_quote_requested || ''} onChange={handleChange} style={dateInputStyle} />}</div>
              <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_received} onChange={() => handleCheckboxChange('quote_received', 'date_quote_received')} style={checkboxStyle} />ðŸ“¬ Offerte ontvangen</label>{checkboxes.quote_received && <input type="date" name="date_quote_received" value={formData.date_quote_received || ''} onChange={handleChange} style={dateInputStyle} />}</div>
              <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.order_placed} onChange={() => handleCheckboxChange('order_placed', 'date_order_placed')} style={checkboxStyle} />ðŸ›’ Bestelling geplaatst</label>{checkboxes.order_placed && <input type="date" name="date_order_placed" value={formData.date_order_placed || ''} onChange={handleChange} style={dateInputStyle} />}</div>

              {checkboxes.order_placed && (
                <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.payment_completed} onChange={() => handleCheckboxChange('payment_completed', 'date_payment_completed')} style={checkboxStyle} />ðŸ’³ Betaling voltooid</label>{checkboxes.payment_completed && <input type="date" name="date_payment_completed" value={formData.date_payment_completed || ''} onChange={handleChange} style={dateInputStyle} />}</div>
              )}

              {checkboxes.payment_completed && (
                <div style={checkboxWrapperStyle}>
                  <label style={checkboxContainerStyle}><input type="checkbox" name="invoice_received" checked={checkboxes.invoice_received} onChange={() => handleCheckboxChange('invoice_received', 'date_invoice_received')} style={checkboxStyle} />ðŸ§¾ Factuur ontvangen</label>{checkboxes.invoice_received && <input type="date" name="date_invoice_received" value={formData.date_invoice_received || ''} onChange={handleChange} style={dateInputStyle} />}
                </div>
              )}
              {checkboxes.payment_completed && (
                <div style={checkboxWrapperStyle}>
                  <label style={checkboxContainerStyle}><input type="checkbox" name="shipped" checked={checkboxes.shipped} onChange={() => handleCheckboxChange('shipped', 'date_shipped')} style={checkboxStyle} />âœˆï¸ Verzonden</label>{checkboxes.shipped && <input type="date" name="date_shipped" value={formData.date_shipped || ''} onChange={handleChange} style={dateInputStyle} />}
                </div>
              )}
              {checkboxes.shipped && (
                <div style={checkboxWrapperStyle}>
                  <label style={checkboxContainerStyle}><input type="checkbox" name="import_invoice_received" checked={checkboxes.import_invoice_received} onChange={() => handleCheckboxChange('import_invoice_received', 'date_import_invoice_received')} style={checkboxStyle} />ðŸšš Importfactuur ontvangen</label>{checkboxes.import_invoice_received && <input type="date" name="date_import_invoice_received" value={formData.date_import_invoice_received || ''} onChange={handleChange} style={dateInputStyle} />}
                </div>
              )}
              {checkboxes.shipped && (
                <div style={checkboxWrapperStyle}>
                  <label style={checkboxContainerStyle}><input type="checkbox" name="received" checked={checkboxes.received} onChange={() => handleCheckboxChange('received', 'date_received')} style={checkboxStyle} />ðŸ“¦ Ontvangen</label>{checkboxes.received && <input type="date" name="date_received" value={formData.date_received || ''} onChange={handleChange} style={dateInputStyle} />}
                </div>
              )}

              {checkboxes.received && (
                <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quality_control} onChange={() => handleCheckboxChange('quality_control', 'date_quality_control')} style={checkboxStyle} />âœ… Kwaliteit akkoord</label>{checkboxes.quality_control && <input type="date" name="date_quality_control" value={formData.date_quality_control || ''} onChange={handleChange} style={dateInputStyle} />}</div>
              )}

              {checkboxes.quality_control && (
                <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.booked_accounting} onChange={() => handleCheckboxChange('booked_accounting', 'date_booked_accounting')} style={checkboxStyle} />ðŸ“Š Admin verwerkt</label>{checkboxes.booked_accounting && <input type="date" name="date_booked_accounting" value={formData.date_booked_accounting || ''} onChange={handleChange} style={dateInputStyle} />}</div>
              )}

              <div style={{ gridColumn: '1 / -1', marginTop: '10px', borderTop: '1px solid #e9ecef', paddingTop: '10px' }}>
                <label style={checkboxContainerStyle}>
                  <input type="checkbox" name="is_return" checked={!!formData.is_return} onChange={handleChange} style={checkboxStyle} />
                  â†©ï¸ Gaat retour
                </label>
              </div>

              {!!formData.is_return && (
                <>
                  <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.is_returned} onChange={() => handleCheckboxChange('is_returned', 'date_is_returned')} style={checkboxStyle} />âœ… Is geretourneerd</label>{checkboxes.is_returned && <input type="date" name="date_is_returned" value={formData.date_is_returned || ''} onChange={handleChange} style={dateInputStyle} />}</div>
                  <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.refunded} onChange={() => handleCheckboxChange('refunded', 'date_refunded')} style={checkboxStyle} />ðŸ’° Bedrag terug ontvangen</label>{checkboxes.refunded && <input type="date" name="date_refunded" value={formData.date_refunded || ''} onChange={handleChange} style={dateInputStyle} />}</div>
                  <div style={checkboxWrapperStyle}><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.return_processed_admin} onChange={() => handleCheckboxChange('return_processed_admin', 'date_return_processed_admin')} style={checkboxStyle} />ðŸ“ˆ Retour verwerkt in admin</label>{checkboxes.return_processed_admin && <input type="date" name="date_return_processed_admin" value={formData.date_return_processed_admin || ''} onChange={handleChange} style={dateInputStyle} />}</div>
                </>
              )}
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
            <div>
              <h3 style={formSectionHeaderStyle}>ðŸ’° FinanciÃ«n</h3>
              {renderCurrencyInput('total_amount_excl_vat', 'Totaalbedrag excl. BTW')}
              {renderCurrencyInput('total_vat', 'Totale BTW')}
              {renderCurrencyInput('total_import_duties', 'Totale invoerrechten')}
              {renderCurrencyInput('total_clearance_costs', 'Totale inklaringskosten')}
              {renderCurrencyInput('vat_clearance_costs', 'BTW inklaringskosten')}
            </div>
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <h3 style={formSectionHeaderStyle}>ðŸ“ Omschrijving en opmerkingen</h3>
              <textarea 
                name="description"
                value={formData.description} 
                onChange={handleChange} 
                style={{ ...inputStyle, flexGrow: 1, resize: 'vertical', minHeight: '150px' }} 
              />
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', paddingTop: '20px', marginTop: '15px', borderTop: '1px solid #e9ecef', flexWrap: 'wrap', gap: '10px' }}>
            <button type="button" onClick={handleDelete} style={{ backgroundColor: '#dc3545', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
              ðŸ—‘ï¸ Verwijderen
            </button>
            <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
              <button type="button" onClick={() => navigate('/orders')} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>
                âŒ Annuleren
              </button>
              <button type="submit" disabled={isLoading} style={{ backgroundColor: isLoading ? '#6c757d' : '#007bff', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: isLoading ? 'not-allowed' : 'pointer', fontSize: '13px', fontWeight: '500' }}>
                {isLoading ? 'ðŸ”„ Opslaan...' : 'ðŸ’¾ Opslaan'}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditOrder;
