import React, { useState } from 'react';
import axios from 'axios';

const AddOrder: React.FC = () => {
  const [formData, setFormData] = useState({
    order_number: '', invoice_number: '', date_quote_requested: '',
    date_invoice_received: '', date_order_placed: '', date_payment_completed: '',
    date_shipped: '', date_received: '', total_amount_excl_vat: '0',
    total_vat: '0', total_import_duties: '0', total_clearance_costs: '0',
    vat_clearance_costs: '0', date_booked_accounting: '', supplier: '',
    webshop: '', description: '', is_return: false
  });

  const [checkboxes, setCheckboxes] = useState({
    quote_requested: false, invoice_received: false, order_placed: false,
    payment_completed: false, shipped: false, received: false, booked_accounting: false
  });

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleCheckboxChange = (field: keyof typeof checkboxes, dateField: keyof typeof formData) => {
    const isChecked = !checkboxes[field];
    setCheckboxes(prev => ({ ...prev, [field]: isChecked }));
    setFormData(prev => ({ ...prev, [dateField]: isChecked ? new Date().toISOString().split('T')[0] : '' }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setMessage('');
    setError('');
    setIsLoading(true);

    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/orders', {
        ...formData,
        total_amount_excl_vat: parseFloat(formData.total_amount_excl_vat) || 0,
        total_vat: parseFloat(formData.total_vat) || 0,
        total_import_duties: parseFloat(formData.total_import_duties) || 0,
        total_clearance_costs: parseFloat(formData.total_clearance_costs) || 0,
        vat_clearance_costs: parseFloat(formData.vat_clearance_costs) || 0
      }, { headers: { Authorization: `Bearer ${token}` } });

      setMessage('Bestelling succesvol toegevoegd! Je wordt teruggestuurd...');
      setTimeout(() => { window.location.href = '/orders'; }, 2000);

    } catch (err: any) {
      setError(err.response?.data?.error || 'Bestelling toevoegen mislukt');
    } finally {
      setIsLoading(false);
    }
  };

  const formSectionStyle = { marginBottom: '30px' };
  const formSectionHeaderStyle = { color: '#34495e', marginBottom: '15px', fontSize: '18px', borderBottom: '2px solid #e9ecef', paddingBottom: '10px' };
  const inputStyle = { width: '100%', padding: '10px', border: '1px solid #ced4da', borderRadius: '6px' };
  const labelStyle = { display: 'block', marginBottom: '8px', fontWeight: '500', color: '#495057' };
  const checkboxContainerStyle = { display: 'flex', alignItems: 'center', gap: '8px' };
  const dateInputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', marginTop: '5px' };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '20px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', maxWidth: '1000px', margin: '0 auto', padding: '25px 30px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          <div>
            <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '26px' }}>➕ Nieuwe Bestelling</h1>
            <p style={{ margin: '5px 0 0 0', color: '#6c757d' }}>Voeg een nieuwe bestelling toe aan je systeem</p>
          </div>
          <button onClick={() => window.location.href = '/orders'} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '10px 18px', borderRadius: '6px', cursor: 'pointer', fontSize: '14px', fontWeight: '500' }}>
            ← Terug naar Bestellingen
          </button>
        </div>

        {message && <div style={{ backgroundColor: '#d4edda', color: '#155724', padding: '15px', borderRadius: '6px', marginBottom: '20px', border: '1px solid #c3e6cb' }}>✅ {message}</div>}
        {error && <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '15px', borderRadius: '6px', marginBottom: '20px', border: '1px solid #f5c6cb' }}>⚠️ {error}</div>}

        <form onSubmit={handleSubmit}>
          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>ℹ️ Basis Informatie</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
              <div>
                <label style={labelStyle}>Ordernummer *</label>
                <input type="text" value={formData.order_number} onChange={(e) => setFormData({...formData, order_number: e.target.value})} required style={inputStyle} placeholder="Bijv. ORD-2025-001" />
              </div>
              <div>
                <label style={labelStyle}>Leverancier *</label>
                <input type="text" value={formData.supplier} onChange={(e) => setFormData({...formData, supplier: e.target.value})} required style={inputStyle} placeholder="Naam van de leverancier" />
              </div>
              <div>
                <label style={labelStyle}>Factuurnummer</label>
                <input type="text" value={formData.invoice_number} onChange={(e) => setFormData({...formData, invoice_number: e.target.value})} disabled={!checkboxes.invoice_received} style={{...inputStyle, backgroundColor: checkboxes.invoice_received ? 'white' : '#e9ecef'}} placeholder="Pas beschikbaar na ontvangst factuur" />
              </div>
              <div>
                <label style={labelStyle}>Webshop</label>
                <input type="text" value={formData.webshop} onChange={(e) => setFormData({...formData, webshop: e.target.value})} style={inputStyle} placeholder="Bijv. Alibaba, AliExpress" />
              </div>
            </div>
          </div>

          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>📅 Proces & Datums</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '20px' }}>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_requested} onChange={() => handleCheckboxChange('quote_requested', 'date_quote_requested')} />📋 Offerte aangevraagd</label>{checkboxes.quote_requested && <input type="date" value={formData.date_quote_requested} onChange={(e) => setFormData({...formData, date_quote_requested: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.order_placed} onChange={() => handleCheckboxChange('order_placed', 'date_order_placed')} />🛒 Order geplaatst</label>{checkboxes.order_placed && <input type="date" value={formData.date_order_placed} onChange={(e) => setFormData({...formData, date_order_placed: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.payment_completed} onChange={() => handleCheckboxChange('payment_completed', 'date_payment_completed')} />💳 Betaling voltooid</label>{checkboxes.payment_completed && <input type="date" value={formData.date_payment_completed} onChange={(e) => setFormData({...formData, date_payment_completed: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.invoice_received} onChange={() => handleCheckboxChange('invoice_received', 'date_invoice_received')} />🧾 Factuur ontvangen</label>{checkboxes.invoice_received && <input type="date" value={formData.date_invoice_received} onChange={(e) => setFormData({...formData, date_invoice_received: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.shipped} onChange={() => handleCheckboxChange('shipped', 'date_shipped')} />📦 Verzonden</label>{checkboxes.shipped && <input type="date" value={formData.date_shipped} onChange={(e) => setFormData({...formData, date_shipped: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.received} onChange={() => handleCheckboxChange('received', 'date_received')} />✅ Ontvangen</label>{checkboxes.received && <input type="date" value={formData.date_received} onChange={(e) => setFormData({...formData, date_received: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.booked_accounting} onChange={() => handleCheckboxChange('booked_accounting', 'date_booked_accounting')} />📊 Geboekt in administratie</label>{checkboxes.booked_accounting && <input type="date" value={formData.date_booked_accounting} onChange={(e) => setFormData({...formData, date_booked_accounting: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={formData.is_return} onChange={(e) => setFormData({...formData, is_return: e.target.checked})} />🔄 Retour</label></div>
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '20px' }}>
            <div>
              <h3 style={formSectionHeaderStyle}>💰 Financiën</h3>
              <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
                  <div><label style={labelStyle}>Totaalbedrag excl. BTW</label><input type="number" step="0.01" value={formData.total_amount_excl_vat} onChange={(e) => setFormData({...formData, total_amount_excl_vat: e.target.value})} style={inputStyle} placeholder="0.00" /></div>
                  <div><label style={labelStyle}>Totale BTW</label><input type="number" step="0.01" value={formData.total_vat} onChange={(e) => setFormData({...formData, total_vat: e.target.value})} style={inputStyle} placeholder="0.00" /></div>
                  <div><label style={labelStyle}>Totale invoerrechten</label><input type="number" step="0.01" value={formData.total_import_duties} onChange={(e) => setFormData({...formData, total_import_duties: e.target.value})} style={inputStyle} placeholder="0.00" /></div>
                  <div><label style={labelStyle}>Totale inklaringskosten</label><input type="number" step="0.01" value={formData.total_clearance_costs} onChange={(e) => setFormData({...formData, total_clearance_costs: e.target.value})} style={inputStyle} placeholder="0.00" /></div>
                  <div><label style={labelStyle}>BTW inklaringskosten</label><input type="number" step="0.01" value={formData.vat_clearance_costs} onChange={(e) => setFormData({...formData, vat_clearance_costs: e.target.value})} style={inputStyle} placeholder="0.00" /></div>
              </div>
            </div>
            <div>
              <h3 style={formSectionHeaderStyle}>📝 Omschrijving en opmerkingen</h3>
              <textarea value={formData.description} onChange={(e) => setFormData({...formData, description: e.target.value})} style={{ ...inputStyle, height: '350px', resize: 'vertical' }} placeholder="Beschrijf de bestelling..." />
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '15px', paddingTop: '20px', marginTop: '20px', borderTop: '1px solid #e9ecef' }}>
            <button type="button" onClick={() => window.location.href = '/orders'} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '12px 24px', borderRadius: '6px', cursor: 'pointer', fontSize: '14px', fontWeight: '500' }}>❌ Annuleren</button>
            <button type="submit" disabled={isLoading} style={{ backgroundColor: isLoading ? '#6c757d' : '#28a745', color: 'white', border: 'none', padding: '12px 24px', borderRadius: '6px', cursor: isLoading ? 'not-allowed' : 'pointer', fontSize: '14px', fontWeight: '500' }}>
              {isLoading ? '🔄 Toevoegen...' : '💾 Bestelling Toevoegen'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddOrder;
