import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Order {
  id: number;
  order_number: string;
  supplier: string;
  total_amount_excl_vat: number;
  status: string;
  date_received: string;
}

const formatCurrency = (amount: number | null | undefined) => {
  if (amount === null || amount === undefined) {
    return '€ 0,00';
  }
  return new Intl.NumberFormat('nl-NL', {
    style: 'currency',
    currency: 'EUR',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
  }).format(amount);
};

const Orders: React.FC = () => {
  const [orders, setOrders] = useState<Order[]>([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('/api/orders', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setOrders(response.data);
      } catch (err: any) {
        setError(err.response?.data?.error || 'Kon orders niet ophalen');
      }
    };
    fetchOrders();
  }, []);

  const getStatusStyle = (status: string) => {
    const baseStyle = {
      padding: '4px 10px',
      borderRadius: '12px',
      fontSize: '12px',
      fontWeight: '500',
      textAlign: 'center' as const,
      color: 'white',
    };

    switch (status.toLowerCase()) {
      case 'compleet': return { ...baseStyle, backgroundColor: '#28a745' };
      case 'in behandeling': return { ...baseStyle, backgroundColor: '#ffc107', color: '#333' };
      case 'geannuleerd': return { ...baseStyle, backgroundColor: '#dc3545' };
      case 'retour': return { ...baseStyle, backgroundColor: '#6f42c1' };
      default: return { ...baseStyle, backgroundColor: '#6c757d' };
    }
  };

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '20px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', maxWidth: '1200px', margin: '0 auto', padding: '25px 30px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
          <div>
            <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '26px' }}>📦 Bestellingen</h1>
            <p style={{ margin: '5px 0 0 0', color: '#6c757d' }}>Overzicht van al je bestellingen</p>
          </div>
          <button onClick={() => navigate('/add-order')} style={{ backgroundColor: '#007bff', color: 'white', border: 'none', padding: '12px 20px', borderRadius: '6px', cursor: 'pointer', fontSize: '14px', fontWeight: '500' }}>
            ➕ Nieuwe Bestelling
          </button>
        </div>

        {error && <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '15px', borderRadius: '6px', marginBottom: '20px', border: '1px solid #f5c6cb' }}>⚠️ {error}</div>}

        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ borderBottom: '2px solid #dee2e6' }}>
              <th style={{ padding: '12px', textAlign: 'left', color: '#495057' }}>Ordernummer</th>
              <th style={{ padding: '12px', textAlign: 'left', color: '#495057' }}>Leverancier</th>
              <th style={{ padding: '12px', textAlign: 'left', color: '#495057' }}>Totaalbedrag (excl. BTW)</th>
              <th style={{ padding: '12px', textAlign: 'left', color: '#495057' }}>Datum Ontvangen</th>
              <th style={{ padding: '12px', textAlign: 'center', color: '#495057' }}>Status</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order.id} style={{ borderBottom: '1px solid #e9ecef', cursor: 'pointer' }} onClick={() => navigate(`/orders/${order.id}`)}>
                <td style={{ padding: '12px', fontWeight: '500', color: '#007bff' }}>{order.order_number}</td>
                <td style={{ padding: '12px' }}>{order.supplier}</td>
                <td style={{ padding: '12px' }}>{formatCurrency(order.total_amount_excl_vat)}</td>
                <td style={{ padding: '12px' }}>{order.date_received ? new Date(order.date_received).toLocaleDateString('nl-NL') : 'N/A'}</td>
                <td style={{ padding: '12px' }}><div style={{ display: 'flex', justifyContent: 'center' }}><span style={getStatusStyle(order.status)}>{order.status}</span></div></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Orders;
