import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Orders: React.FC = () => {
  const [orders, setOrders] = useState<any[]>([]);
  const [error, setError] = useState('');
  const [selectedOrder, setSelectedOrder] = useState<any>(null);
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/orders', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data);
    } catch (err: any) {
      setError('Kan bestellingen niet ophalen');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  const handleOrderClick = (order: any) => {
    setSelectedOrder(order);
    setShowEditModal(true);
  };

  const calculateStatus = (order: any) => {
    if (order.is_return) {
      let baseStatus = 'Nieuw';
      if (order.date_received) baseStatus = 'Ontvangen';
      else if (order.date_shipped) baseStatus = 'Verzonden';
      else if (order.date_payment_completed) baseStatus = 'Besteld en betaald';
      else if (order.date_order_placed) baseStatus = 'Besteld (nog niet betaald)';
      else if (order.date_quote_requested) baseStatus = 'Offerte aangevraagd';
      return baseStatus + ' (Gaat retour)';
    }

    if (order.date_received) return 'Ontvangen';
    if (order.date_shipped) return 'Verzonden';
    if (order.date_payment_completed) return 'Besteld en betaald';
    if (order.date_order_placed) return 'Besteld (nog niet betaald)';
    if (order.date_quote_requested) return 'Offerte aangevraagd';
    return 'Nieuw';
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2>Bestellingen</h2>
        <div>
          <a href="/add-order" style={{ color: '#28a745', textDecoration: 'none', marginRight: '15px', fontWeight: 'bold' }}>
            + Nieuwe Bestelling
          </a>
          <a href="/change-password" style={{ color: '#007bff', textDecoration: 'none', marginRight: '15px' }}>
            Wachtwoord Wijzigen
          </a>
          <button 
            onClick={handleLogout}
            style={{ 
              backgroundColor: '#dc3545', 
              color: 'white', 
              border: 'none', 
              padding: '8px 16px', 
              cursor: 'pointer' 
            }}
          >
            Uitloggen
          </button>
        </div>
      </div>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <table style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Ordernummer</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Leverancier</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Webshop</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Omschrijving</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Totaal excl. BTW</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Status</th>
            <th style={{ border: '1px solid #ddd', padding: '8px' }}>Retour</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td 
                style={{ 
                  border: '1px solid #ddd', 
                  padding: '8px', 
                  cursor: 'pointer',
                  color: '#007bff',
                  textDecoration: 'underline'
                }}
                onClick={() => handleOrderClick(order)}
              >
                {order.order_number}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{order.supplier}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{order.webshop}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>{order.description}</td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                €{order.total_amount_excl_vat || order.total_price || 0}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {calculateStatus(order)}
              </td>
              <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                {order.is_return ? '✓' : ''}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Modal */}
      {showEditModal && selectedOrder && (
        <EditOrderModal 
          order={selectedOrder} 
          onClose={() => setShowEditModal(false)}
          onSave={fetchOrders}
        />
      )}
    </div>
  );
};

// Edit Order Modal Component
const EditOrderModal: React.FC<{ order: any; onClose: () => void; onSave: () => void }> = ({ order, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    order_number: order.order_number || '',
    invoice_number: order.invoice_number || '',
    date_quote_requested: order.date_quote_requested || '',
    date_invoice_received: order.date_invoice_received || '',
    date_order_placed: order.date_order_placed || '',
    date_payment_completed: order.date_payment_completed || '',
    date_shipped: order.date_shipped || '',
    date_received: order.date_received || '',
    total_amount_excl_vat: order.total_amount_excl_vat || '',
    total_vat: order.total_vat || '',
    total_import_duties: order.total_import_duties || '',
    total_clearance_costs: order.total_clearance_costs || '',
    vat_clearance_costs: order.vat_clearance_costs || '',
    date_booked_accounting: order.date_booked_accounting || '',
    supplier: order.supplier || '',
    webshop: order.webshop || '',
    description: order.description || '',
    is_return: order.is_return || false
  });

  const [checkboxes, setCheckboxes] = useState({
    quote_requested: !!order.date_quote_requested,
    invoice_received: !!order.date_invoice_received,
    order_placed: !!order.date_order_placed,
    payment_completed: !!order.date_payment_completed,
    shipped: !!order.date_shipped,
    received: !!order.date_received,
    booked_accounting: !!order.date_booked_accounting
  });

  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  const handleCheckboxChange = (field: keyof typeof checkboxes) => {
    setCheckboxes(prev => ({
      ...prev,
      [field]: !prev[field]
    }));

    if (!checkboxes[field]) {
      // Checkbox wordt aangevinkt, zet huidige datum
      const today = new Date().toISOString().split('T')[0];
      setFormData(prev => ({
        ...prev,
        [field]: today
      }));
    } else {
      // Checkbox wordt uitgevinkt, verwijder datum
      setFormData(prev => ({
        ...prev,
        [field]: ''
      }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.put(`/api/orders/${order.id}`, formData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      onSave();
      onClose();
    } catch (err: any) {
      alert('Fout bij opslaan: ' + (err.response?.data?.error || 'Onbekende fout'));
    }
  };

  const handleDelete = async () => {
    if (!showDeleteConfirm) {
      setShowDeleteConfirm(true);
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/orders/${order.id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      onSave();
      onClose();
    } catch (err: any) {
      alert('Fout bij verwijderen: ' + (err.response?.data?.error || 'Onbekende fout'));
    }
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        maxWidth: '800px',
        maxHeight: '90vh',
        overflow: 'auto',
        width: '100%'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h3>Order Bewerken: {order.order_number}</h3>
          <button
            onClick={handleDelete}
            style={{
              backgroundColor: showDeleteConfirm ? '#dc3545' : '#ffc107',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              cursor: 'pointer',
              borderRadius: '4px'
            }}
          >
            {showDeleteConfirm ? 'Bevestig Verwijderen' : 'Verwijderen'}
          </button>
        </div>

        {showDeleteConfirm && (
          <div style={{ 
            backgroundColor: '#fff3cd', 
            border: '1px solid #ffeaa7', 
            padding: '10px', 
            marginBottom: '15px',
            borderRadius: '4px'
          }}>
            <strong>Waarschuwing:</strong> Weet je zeker dat je deze order wilt verwijderen? 
            Deze actie kan niet ongedaan worden gemaakt.
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
            {/* Basis informatie */}
            <div>
              <label>Ordernummer *</label>
              <input
                type="text"
                value={formData.order_number}
                onChange={(e) => setFormData({...formData, order_number: e.target.value})}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Leverancier *</label>
              <input
                type="text"
                value={formData.supplier}
                onChange={(e) => setFormData({...formData, supplier: e.target.value})}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Webshop</label>
              <input
                type="text"
                value={formData.webshop}
                onChange={(e) => setFormData({...formData, webshop: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Factuurnummer</label>
              <input
                type="text"
                value={formData.invoice_number}
                onChange={(e) => setFormData({...formData, invoice_number: e.target.value})}
                disabled={!checkboxes.invoice_received}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            {/* Datums met checkboxes */}
            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.quote_requested}
                  onChange={() => handleCheckboxChange('quote_requested')}
                />
                Datum offerte aangevraagd
              </label>
              {checkboxes.quote_requested && (
                <input
                  type="date"
                  value={formData.date_quote_requested}
                  onChange={(e) => setFormData({...formData, date_quote_requested: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.invoice_received}
                  onChange={() => handleCheckboxChange('invoice_received')}
                />
                Datum factuur ontvangen
              </label>
              {checkboxes.invoice_received && (
                <input
                  type="date"
                  value={formData.date_invoice_received}
                  onChange={(e) => setFormData({...formData, date_invoice_received: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.order_placed}
                  onChange={() => handleCheckboxChange('order_placed')}
                />
                Datum order geplaatst
              </label>
              {checkboxes.order_placed && (
                <input
                  type="date"
                  value={formData.date_order_placed}
                  onChange={(e) => setFormData({...formData, date_order_placed: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.payment_completed}
                  onChange={() => handleCheckboxChange('payment_completed')}
                />
                Datum betaling voltooid
              </label>
              {checkboxes.payment_completed && (
                <input
                  type="date"
                  value={formData.date_payment_completed}
                  onChange={(e) => setFormData({...formData, date_payment_completed: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.shipped}
                  onChange={() => handleCheckboxChange('shipped')}
                />
                Datum verzonden
              </label>
              {checkboxes.shipped && (
                <input
                  type="date"
                  value={formData.date_shipped}
                  onChange={(e) => setFormData({...formData, date_shipped: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.received}
                  onChange={() => handleCheckboxChange('received')}
                />
                Datum ontvangen
              </label>
              {checkboxes.received && (
                <input
                  type="date"
                  value={formData.date_received}
                  onChange={(e) => setFormData({...formData, date_received: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={checkboxes.booked_accounting}
                  onChange={() => handleCheckboxChange('booked_accounting')}
                />
                Datum geboekt in administratie
              </label>
              {checkboxes.booked_accounting && (
                <input
                  type="date"
                  value={formData.date_booked_accounting}
                  onChange={(e) => setFormData({...formData, date_booked_accounting: e.target.value})}
                  style={{ width: '100%', padding: '8px', marginTop: '5px' }}
                />
              )}
            </div>

            {/* Bedragen */}
            <div>
              <label>Totaalbedrag excl. BTW</label>
              <input
                type="number"
                step="0.01"
                value={formData.total_amount_excl_vat}
                onChange={(e) => setFormData({...formData, total_amount_excl_vat: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Totale BTW</label>
              <input
                type="number"
                step="0.01"
                value={formData.total_vat}
                onChange={(e) => setFormData({...formData, total_vat: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Totale invoerrechten</label>
              <input
                type="number"
                step="0.01"
                value={formData.total_import_duties}
                onChange={(e) => setFormData({...formData, total_import_duties: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>Totale inklaringskosten</label>
              <input
                type="number"
                step="0.01"
                value={formData.total_clearance_costs}
                onChange={(e) => setFormData({...formData, total_clearance_costs: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>BTW inklaringskosten</label>
              <input
                type="number"
                step="0.01"
                value={formData.vat_clearance_costs}
                onChange={(e) => setFormData({...formData, vat_clearance_costs: e.target.value})}
                style={{ width: '100%', padding: '8px' }}
              />
            </div>

            <div>
              <label>
                <input
                  type="checkbox"
                  checked={formData.is_return}
                  onChange={(e) => setFormData({...formData, is_return: e.target.checked})}
                />
                Retour
              </label>
            </div>
          </div>

          <div style={{ gridColumn: '1 / -1', marginTop: '15px' }}>
            <label>Omschrijving</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              style={{ width: '100%', padding: '8px', height: '80px' }}
            />
          </div>

          <div style={{ marginTop: '20px', textAlign: 'right' }}>
            <button
              type="button"
              onClick={onClose}
              style={{
                backgroundColor: '#6c757d',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                marginRight: '10px',
                cursor: 'pointer'
              }}
            >
              Annuleren
            </button>
            <button
              type="submit"
              style={{
                backgroundColor: '#28a745',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                cursor: 'pointer'
              }}
            >
              Opslaan
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Orders;
