import React, { useState } from 'react';
import axios from 'axios';

const AddOrder: React.FC = () => {
  const [formData, setFormData] = useState({
    order_number: '',
    invoice_number: '',
    date_quote_requested: '',
    date_quote_received: '',
    date_order_placed: '',
    date_payment_completed: '',
    date_invoice_received: '',
    date_shipped: '',
    date_received: '',
    date_quality_control: '',
    date_booked_accounting: '',
    date_is_returned: '',
    date_refunded: '',
    date_return_processed_admin: '',
    total_amount_excl_vat: '',
    total_vat: '',
    total_import_duties: '',
    total_clearance_costs: '',
    vat_clearance_costs: '',
    supplier: '',
    webshop: '',
    description: '',
    is_return: false
  });

  const [checkboxes, setCheckboxes] = useState({
    quote_requested: false,
    quote_received: false,
    order_placed: false,
    payment_completed: false,
    invoice_received: false,
    shipped: false,
    received: false,
    quality_control: false,
    booked_accounting: false,
    is_returned: false,
    refunded: false,
    return_processed_admin: false,
  });

  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Helper om een NL-string ("1.234,56") om te zetten naar een JS-nummer (1234.56)
  const parseLocaleString = (str: string): number | null => {
    if (typeof str !== 'string' || str.trim() === '') return null;
    const number = parseFloat(str.replace(/\./g, '').replace(',', '.'));
    return isNaN(number) ? null : number;
  };

  // Helper om een JS-nummer (1234.56) te formatteren naar een NL-string ("1.234,56")
  const formatToLocaleString = (num: number | null): string => {
    if (num === null || num === undefined || num === 0) return '';
    return new Intl.NumberFormat('nl-NL', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(num);
  };

  const handleCheckboxChange = (field: keyof typeof checkboxes, dateField: keyof typeof formData) => {
    const isChecked = !checkboxes[field];
    setCheckboxes(prev => ({ ...prev, [field]: isChecked }));
    setFormData(prev => ({ ...prev, [dateField]: isChecked ? new Date().toISOString().split('T')[0] : '' }));
  };

  // Update de state direct met de (ongeformatteerde) input van de gebruiker
  const handleCurrencyChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Bij het verlaten van het veld, parse en formatteer de waarde
  const handleCurrencyBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const numericValue = parseLocaleString(value);
    setFormData(prev => ({ ...prev, [name]: formatToLocaleString(numericValue) }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setMessage('');
    setError('');
    setIsLoading(true);

    const dataToSubmit = {
      ...formData,
      total_amount_excl_vat: parseLocaleString(formData.total_amount_excl_vat) || 0,
      total_vat: parseLocaleString(formData.total_vat) || 0,
      total_import_duties: parseLocaleString(formData.total_import_duties) || 0,
      total_clearance_costs: parseLocaleString(formData.total_clearance_costs) || 0,
      vat_clearance_costs: parseLocaleString(formData.vat_clearance_costs) || 0
    };

    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/orders', dataToSubmit, { headers: { Authorization: `Bearer ${token}` } });

      setMessage('Bestelling succesvol toegevoegd! Je wordt teruggestuurd...');
      setTimeout(() => { window.location.href = '/orders'; }, 2000);

    } catch (err: any) {
      setError(err.response?.data?.error || 'Toevoegen van bestelling mislukt');
    } finally {
      setIsLoading(false);
    }
  };

  const formSectionStyle = { marginBottom: '20px' };
  const formSectionHeaderStyle = { color: '#34495e', marginBottom: '15px', fontSize: '16px', borderBottom: '1px solid #e9ecef', paddingBottom: '8px' };
  const inputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', boxSizing: 'border-box' as const, fontSize: '14px' };
  const labelStyle = { display: 'block', marginBottom: '5px', fontWeight: '500', color: '#495057', fontSize: '13px' };
  const checkboxStyle = { marginRight: '8px', transform: 'scale(1.2)' };
  const checkboxContainerStyle = { display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '10px' };
  const dateInputStyle = { width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '6px', marginTop: '5px' };

  const renderCurrencyInput = (name: keyof typeof formData, label: string) => (
    <div style={{ marginBottom: '15px' }}>
      <label style={labelStyle}>{label}</label>
      <input
        type="text"
        inputMode="decimal"
        name={name}
        value={formData[name] !== undefined && formData[name] !== null && formData[name] !== '' && formData[name] !== '0' && formData[name] !== '0,00' ? String(formData[name]) : ''}
        onChange={handleCurrencyChange}
        onBlur={handleCurrencyBlur}
        style={inputStyle}
        placeholder="0,00"
      />
    </div>
  );

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa', padding: '10px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif' }}>
      <div style={{ backgroundColor: 'white', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)', padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '20px'}}>
            <img src="/logo.png" alt="Lion Gris Logo" style={{ width: '50px', height: 'auto' }} />
            <div>
              <h1 style={{ margin: 0, color: '#2c3e50', fontSize: '18px' }}>â• Nieuwe Bestelling Toevoegen</h1>
              <p style={{ margin: '2px 0 0 0', color: '#6c757d', fontSize: '12px' }}>Voer de details van de nieuwe bestelling in</p>
            </div>
        </div>
        <form onSubmit={handleSubmit}>
          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>â„¹ï¸ Basis Informatie</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
              <div>
                <label style={labelStyle}>Ordernummer *</label>
                <input type="text" name="order_number" value={formData.order_number} onChange={(e) => setFormData({...formData, order_number: e.target.value})} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Factuurnummer</label>
                <input type="text" name="invoice_number" value={formData.invoice_number} onChange={(e) => setFormData({...formData, invoice_number: e.target.value})} style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Leverancier *</label>
                <input type="text" name="supplier" value={formData.supplier} onChange={(e) => setFormData({...formData, supplier: e.target.value})} required style={inputStyle} />
              </div>
              <div>
                <label style={labelStyle}>Webshop</label>
                <input type="text" name="webshop" value={formData.webshop} onChange={(e) => setFormData({...formData, webshop: e.target.value})} style={inputStyle} />
              </div>
            </div>
          </div>

          <div style={formSectionStyle}>
            <h3 style={formSectionHeaderStyle}>ğŸ“… Proces & Datums</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_requested} onChange={() => handleCheckboxChange('quote_requested', 'date_quote_requested')} style={checkboxStyle} />ğŸ“‹ Offerte aangevraagd</label>{checkboxes.quote_requested && <input type="date" value={formData.date_quote_requested} onChange={(e) => setFormData({...formData, date_quote_requested: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quote_received} onChange={() => handleCheckboxChange('quote_received', 'date_quote_received')} style={checkboxStyle} />ğŸ“¬ Offerte ontvangen</label>{checkboxes.quote_received && <input type="date" value={formData.date_quote_received} onChange={(e) => setFormData({...formData, date_quote_received: e.target.value})} style={dateInputStyle} />}</div>
                <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.order_placed} onChange={() => handleCheckboxChange('order_placed', 'date_order_placed')} style={checkboxStyle} />ğŸ›’ Bestelling geplaatst</label>{checkboxes.order_placed && <input type="date" value={formData.date_order_placed} onChange={(e) => setFormData({...formData, date_order_placed: e.target.value})} style={dateInputStyle} />}</div>

                {checkboxes.order_placed && (
                  <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.payment_completed} onChange={() => handleCheckboxChange('payment_completed', 'date_payment_completed')} style={checkboxStyle} />ğŸ’³ Betaling voltooid</label>{checkboxes.payment_completed && <input type="date" value={formData.date_payment_completed} onChange={(e) => setFormData({...formData, date_payment_completed: e.target.value})} style={dateInputStyle} />}</div>
                )}

                {checkboxes.payment_completed && (
                  <>
                    <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.invoice_received} onChange={() => handleCheckboxChange('invoice_received', 'date_invoice_received')} style={checkboxStyle} />ğŸ§¾ Factuur ontvangen</label>{checkboxes.invoice_received && <input type="date" value={formData.date_invoice_received} onChange={(e) => setFormData({...formData, date_invoice_received: e.target.value})} style={dateInputStyle} />}</div>
                    <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.shipped} onChange={() => handleCheckboxChange('shipped', 'date_shipped')} style={checkboxStyle} />ğŸ“¦ Verzonden</label>{checkboxes.shipped && <input type="date" value={formData.date_shipped} onChange={(e) => setFormData({...formData, date_shipped: e.target.value})} style={dateInputStyle} />}</div>
                  </>
                )}

                {checkboxes.shipped && (
                  <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.received} onChange={() => handleCheckboxChange('received', 'date_received')} style={checkboxStyle} />âœ… Ontvangen</label>{checkboxes.received && <input type="date" value={formData.date_received} onChange={(e) => setFormData({...formData, date_received: e.target.value})} style={dateInputStyle} />}</div>
                )}

                {checkboxes.received && (
                  <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.quality_control} onChange={() => handleCheckboxChange('quality_control', 'date_quality_control')} style={checkboxStyle} />ğŸ”¬ Kwaliteitscontrole</label>{checkboxes.quality_control && <input type="date" value={formData.date_quality_control} onChange={(e) => setFormData({...formData, date_quality_control: e.target.value})} style={dateInputStyle} />}</div>
                )}

                {checkboxes.quality_control && (
                  <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.booked_accounting} onChange={() => handleCheckboxChange('booked_accounting', 'date_booked_accounting')} style={checkboxStyle} />ğŸ“Š Admin verwerkt</label>{checkboxes.booked_accounting && <input type="date" value={formData.date_booked_accounting} onChange={(e) => setFormData({...formData, date_booked_accounting: e.target.value})} style={dateInputStyle} />}</div>
                )}

                <div style={{ gridColumn: '1 / -1', marginTop: '10px', borderTop: '1px solid #e9ecef', paddingTop: '10px' }}>
                  <label style={checkboxContainerStyle}>
                    <input type="checkbox" name="is_return" checked={formData.is_return} onChange={(e) => setFormData({...formData, is_return: e.target.checked})} style={checkboxStyle} />
                    â†©ï¸ Gaat retour
                  </label>
                </div>

                {formData.is_return && (
                  <>
                    <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.is_returned} onChange={() => handleCheckboxChange('is_returned', 'date_is_returned')} style={checkboxStyle} />âœ… Is geretourneerd</label>{checkboxes.is_returned && <input type="date" value={formData.date_is_returned} onChange={(e) => setFormData({...formData, date_is_returned: e.target.value})} style={dateInputStyle} />}</div>
                    <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.refunded} onChange={() => handleCheckboxChange('refunded', 'date_refunded')} style={checkboxStyle} />ğŸ’° Bedrag terug ontvangen</label>{checkboxes.refunded && <input type="date" value={formData.date_refunded} onChange={(e) => setFormData({...formData, date_refunded: e.target.value})} style={dateInputStyle} />}</div>
                    <div><label style={checkboxContainerStyle}><input type="checkbox" checked={checkboxes.return_processed_admin} onChange={() => handleCheckboxChange('return_processed_admin', 'date_return_processed_admin')} style={checkboxStyle} />ğŸ“ˆ Retour verwerkt in admin</label>{checkboxes.return_processed_admin && <input type="date" value={formData.date_return_processed_admin} onChange={(e) => setFormData({...formData, date_return_processed_admin: e.target.value})} style={dateInputStyle} />}</div>
                  </>
                )}
            </div>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
            <div>
              <h3 style={formSectionHeaderStyle}>ğŸ’° FinanciÃ«n</h3>
              {renderCurrencyInput('total_amount_excl_vat', 'Totaalbedrag excl. BTW')}
              {renderCurrencyInput('total_vat', 'Totale BTW')}
              {renderCurrencyInput('total_import_duties', 'Totale invoerrechten')}
              {renderCurrencyInput('total_clearance_costs', 'Totale inklaringskosten')}
              {renderCurrencyInput('vat_clearance_costs', 'BTW inklaringskosten')}
            </div>
            <div style={{ display: 'flex', flexDirection: 'column' }}>
              <h3 style={formSectionHeaderStyle}>ğŸ“ Omschrijving en opmerkingen</h3>
              <textarea 
                value={formData.description} 
                onChange={(e) => setFormData({...formData, description: e.target.value})} 
                style={{ ...inputStyle, flexGrow: 1, resize: 'vertical', minHeight: '150px' }} 
                placeholder="Beschrijf de bestelling..." 
              />
            </div>
          </div>

          <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px', paddingTop: '20px', marginTop: '15px', borderTop: '1px solid #e9ecef', flexWrap: 'wrap' }}>
            <button type="button" onClick={() => window.location.href = '/orders'} style={{ backgroundColor: '#6c757d', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: 'pointer', fontSize: '13px', fontWeight: '500' }}>âŒ Annuleren</button>
            <button type="submit" disabled={isLoading} style={{ backgroundColor: isLoading ? '#6c757d' : '#28a745', color: 'white', border: 'none', padding: '8px 16px', borderRadius: '6px', cursor: isLoading ? 'not-allowed' : 'pointer', fontSize: '13px', fontWeight: '500' }}>
              {isLoading ? 'ğŸ”„ Toevoegen...' : 'ğŸ’¾ Toevoegen'}
            </button>
